  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Basic steps',
	slug: '/'
};
export const metadata = {
  "unversionedId": "getting-started",
  "id": "getting-started",
  "isDocsHomePage": false,
  "title": "Basic steps",
  "description": "Create a new project",
  "source": "@site/docs/getting-started.md",
  "slug": "/",
  "permalink": "/docs/",
  "editUrl": "https://github.com/citedrive/citedrive-docs/tree/main/docs/getting-started.md",
  "version": "current",
  "sidebar": "docs",
  "next": {
    "title": "Share project",
    "permalink": "/docs/share-project"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Create a new project',
  id: 'create-a-new-project',
  children: []
}, {
  value: 'Create new references',
  id: 'create-new-references',
  children: []
}, {
  value: 'Organize references',
  id: 'organize-references',
  children: []
}, {
  value: 'Create citations and bibliography',
  id: 'create-citations-and-bibliography',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "create-a-new-project"
    }}>{`Create a new project`}</h2>
    <p>{`After you log in, you will see all your projects in the dashboard overview. These are the ones you may have created or to which your colleagues have invited you.`}</p>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span>{`Example projects`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`When you log in for the first time, we make three sample projects for you to play around with.`}</p></div></div>
    <p>{`To create a new project, click on the `}<inlineCode parentName="p">{`+ Add new project`}</inlineCode>{`-button. A pop-up window should appear, looking like this:`}</p>
    <p><img alt={"Example banner"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./assets/new_project_window.svg").default} /></p>
    <p>{`There you enter a name for your project; this could be a paper or essay title. In our example, we name our project `}<inlineCode parentName="p">{`My first project.`}</inlineCode></p>
    <p>{`You choose the project type in the second step, with the standard "CiteDrive" being the typical use for most users.`}</p>
    <div {...{
      "className": "admonition admonition-info alert alert--info"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M7 2.3c3.14 0 5.7 2.56 5.7 5.7s-2.56 5.7-5.7 5.7A5.71 5.71 0 0 1 1.3 8c0-3.14 2.56-5.7 5.7-5.7zM7 1C3.14 1 0 4.14 0 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm1 3H6v5h2V4zm0 6H6v2h2v-2z"
              }}></path></svg></span>{`info`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`We offer three project types; our standard CiteDrive, BibTeX, and R Markdown; these offer three different modes.
To differentiate:
Suppose you're using Microsoft Word, Google Docs, Apple Pages, OpenOffice, LibreOffice, or similar programs. In that case, the first one is for you.
If you work with LaTeX, then it is best to choose BibTeX.
If you work with Pandoc or R Markdown, choose R Markdown.`}</p></div></div>
    <p>{`With `}<inlineCode parentName="p">{`Create new project`}</inlineCode>{`, when you are ready. We can now see in your new project in the overview. Click on the new project for more in the next step.`}</p>
    <h2 {...{
      "id": "create-new-references"
    }}>{`Create new references`}</h2>
    <p>{`You can click on the big plus sign `}<img alt={"Example banner"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./assets/plus.svg").default} />{` in the panel on the right side to add new references. A pop-up window will appear. Define the type of your source, such as “Article in Journal” (paper / scientific publication), website, piece of music, etc., and enter all the data you need to cite.
Scroll down and click on the button when you have finished. We should have added a new table with further reference.`}</p>
    <p>{`If you want to edit any reference, `}<strong parentName="p">{`hover`}</strong>{` over the table and click on the edit icon.`}</p>
    <p><img alt={"Example banner"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./assets/edit.svg").default} /></p>
    <h2 {...{
      "id": "organize-references"
    }}>{`Organize references`}</h2>
    <p>{`You organize your references in tags that you can create on the left side - just click on the corresponding button. To add references to the tags, choose and click on the related references. On the right side, the panel will change with new options. At the top, you will see an icon with the number of selected references. Drag this icon to the tag.`}</p>
    <h2 {...{
      "id": "create-citations-and-bibliography"
    }}>{`Create citations and bibliography`}</h2>
    <p>{`You can set the output language at the top of the navbar in which the app will generate your bibliography. To the right, you can select the style.
To cite:`}</p>
    <ol>
      <li parentName="ol">{`Select one reference - or multiple if you want to refer to numerous sources to cluster into one.`}</li>
      <li parentName="ol">{`Click on the citation icon, and it will generate the appropriate format for in-text citations.`}</li>
      <li parentName="ol">{`You can now copy and paste it into your text document. For example Microsoft Word.`}</li>
    </ol>
    <p>{`To generate your bibliography, click on `}<inlineCode parentName="p">{`Create Bibliography`}</inlineCode>{` in the navbar, you will be redirected to a page with all sources in the correct style format.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
